import * as actions from '../../middleware/apiGen';
import * as ActionEvent from '../../static/actionsEvent';
const initState = {
  loginName: "not login",
  isLogin: 0,
  curUserId:"",
  curPassword:"",
  toPath:"home-fashion"
};

const loginReducer = (state = initState, action) => {
  console.log(action.type)

  if (action.type === actions.loginSuccess.type) {
    console.log(state);
    console.log(action.payload);


    return {

      loginStatus: action.payload
    };
  }
//   else if(action.type= actions.logoutSccess.type){
// return{
//   ...state,
//   loginStatus:action.payload


// }

//   }
  else if(action.type===actions.loginFailed.type){
    return{

      loginStatus:action.payload

    }

  }
  else if(action.type===actions.loginBegan.type){
    return {
      loginStatus:actions.loginBegan.payload
    }
  }
  else if(action.type === actions.updatePass.type){
    return{
      ...state,
      loginStatus:action.payload

    }


  }
  else if(action.type ===actions.updateUser){
    return{
      ...state,
      loginStatus:action.payload

    }


  }
  else if(action.type === ActionEvent.Login_Redirect_Event){
    return{
      ...state,
      loginStatus:{toPath:action.toPath}

    }
  }

  return state;
};

export default loginReducer;
