import {store} from '../../';
import * as ActionEvent from '../../static/actionsEvent';
export   const LOGIN_SUCESS = "LOGIN_SUCESS";
export   const LOGIN_FAILED = "LOGIN_FAILED";




export const setLoginRedirectUrl = (toPath) => {
	return  store.dispatch({
			 	type: ActionEvent.Login_Redirect_Event,
				toPath: toPath
			 })
} 


export const login = (userName,UserPass) => {
    return dispatch => {
      if (1==0){
        dispatch({
          type: LOGIN_SUCESS,
          payload: { userName,UserPass}
        });
      }
      else{

        dispatch({
          type: LOGIN_FAILED,
          payload: { loginName:"not login",isLogin:0}
        });
      }

    };
  };
