import React from 'react'
import { Route, Redirect } from 'react-router-dom'
import { setLoginRedirectUrl } from '../../redux/actions/loginActions'
import {CheckLogin } from '../../service/CheckLogin'
class AuthorizedRoute extends React.Component {
    render() {
        const { component: Component, ...rest } = this.props
        const isLogged =CheckLogin()  //false //sessionStorage.getItem("userName") != null ? true : false;
        if(isLogged==="approved") {
            setLoginRedirectUrl(this.props.location.pathname);
        }
        return (
                <Route {...rest} render={props => {
                    return isLogged
                            ?  <Component {...props} />
                            : <Redirect to="/login-register" />
                }} />
        )
    }
}

export default AuthorizedRoute
