import AuthenticationService from '../../../service/AuthenticationService'
import * as acitons2 from '../../../middleware/apiGen'
// 引入解析token方法
import jwt_decode from 'jwt-decode'
// 引入设置token方法
import setAuthToken from '../../components/auth/setAuthToken'

export const authenticateUser = (username,pass)=>{

    return function(dispatch){
        dispatch(acitons2.loginBegan)

        AuthenticationService.executeJwtAuthenticationService(username,pass)
        .then(response=>{

            console.log(response);


            if (response.data.message==="login"){

              const token = response.data.token;
              console.log(token)
              localStorage.setItem('jwToken',token)
              //设置axios的headers token
              setAuthToken(token)
              // 解析token
              const decoded = jwt_decode(token)
              dispatch(acitons2.loginSuccess({loginName:"login",isLogin:1} ))



            }
            else{
                dispatch(acitons2.loginSuccess({loginName:"not login",isLogin:0} ))

            }



        })
        .catch(error=>{
            dispatch(acitons2.loginSuccess({loginName:"not login",isLogin:0} ))

        })




    }
}
