import {LOGIN_SUCESS,login,LOGIN_FAILED} from "../actions/loginActions"

const initState = {
  loginName: "not login",
  isLogin: 0
};

const loginReducer = (state = initState, action) => {
  if (action.type === LOGIN_SUCESS) {
    return {
      ...state,
      loginStatus: action.payload
    };
  }
  else if(action.type===LOGIN_FAILED){
