import {LOGIN_SUCESS} from "../actions/loginActions"

const initState = {
  loginStatus: ["not login"]
};

const loginReducer = (state = initState, action) => {
  if (action.type === LOGIN_SUCESS) {
    return {
      ...state,
      products: action.payload
    };
  }

  return state;
};

export default loginReducer;
