import {LOGIN_SUCESS,login,LOGIN_FAILED,UpdatePassword,UpdateUserId} from "../actions/loginActions"
import * as actions from '../../middleware/apiGen';
const initState = {
  loginName: "not login",
  isLogin: 0,
  curUserId:"",
  curPassword:""
};

const loginReducer = (state = initState, action) => {
  console.log(action.type)
  console.log(actions.loginSuccess())
  if (action.type === actions.loginSuccess) {
    console.log(state);
    console.log(action.payload);

    return {

      loginStatus: action.payload
    };
  }
  else if(action.type===actions.loginFailed()){
    return{

      loginStatus:action.payload

    }

  }
  else if(action.type ===UpdatePassword){
    return{
      ...state,
      loginStatus:action.payload

    }


  }
  else if(action.type ===UpdateUserId){
    return{
      ...state,
      loginStatus:action.payload

    }


  }

  return state;
};

export default loginReducer;
