const initState = {

  userId:"",
  passsword:"",
  toPath:"home-fashion",
  loginStatus:{ loginName: "not login",
  isLogin: 0,}
};
 const loginReducer = createReducer(initState,{

  updateUser:(state,action)=>{
    state.push({username:action.payload})

  },
  loginBegan:(state,action)=>{

    state.push({
      loginStatus:{
message:"loginBegan",
