export   const LOGIN_FAILED = "LOGIN_FAILED";

export const UpdatePassword = "updatePassword";
export const UpdateUserId = "updateUserId";

export const setLoginRedirectUrl = (toPath) => {
	return  store.dispatch({
			 	type: ActionEvent.Login_Redirect_Event,
				toPath: toPath
			 })
} 

export const updatePassword = (password)=>{
return store.dispatch({
  type: updatePassword,
  payload: {curPassword:password}

})

}

export const updateUserId = (UserId)=>{
  return store.dispatch({
    type: updateUserId,
    payload: {curUserId:UserId}

  })}
export const logout = ()=>{

  return store.dispatch({
    type:actions.logoutSccess,
    payload:{
      isLogin: 0,
      message:"You have seccessfully logout!"

    }
  })
}
export const login = (userName,UserPass) => {
   console.log("login began");



   store.dispatch({
    type:actions.loginBegan.type,
    payload:{
    url: '/authenticate',
